mapper.select:
SELECT NOTI_SEQ , USER_ID , NOTI_SUBJECT , NOTI_CONTENT , SEEN_YN , MAIL_YN FROM TB_NOTI WHERE 1=1 AND USE_YN = 'Y'
AND NOTI_YN = #{notiYn}
AND USER_ID = #{sessionUserId}
AND GROUP_ID = #{groupId}
ORDER BY NOTI_SEQ DESC

mapper.update:
WITH upsert AS
(
    UPDATE 
        TB_NOTI
    SET 
        UPDR_ID = #{sessionUserId}
        , UPD_DTM = NOW()

        , NOTI_CONTENT = #{notiContent}


        , NOTI_YN = #{notiYn}


        , SEEN_YN = #{seenYn}


        , MAIL_YN = #{mailYn}


        , USE_YN = #{useYn}

    WHERE
        NOTI_SEQ = #{notiSeq}
    RETURNING *
)
INSERT INTO TB_NOTI
(
    NOTI_SEQ
    , NOTI_CONTENT
    , GROUP_ID
    , USER_ID
    , SEEN_YN
    , MAIL_YN
    , USE_YN
    , REGR_ID
    , REG_DTM
    , UPDR_ID
    , UPD_DTM
)
SELECT  
    nextval('TB_NOTI_SEQ')
    , #{notiContent}
    , #{groupId}
    , #{userId}
    , 'N'
    , 'N'
    , 'Y'
    , #{sessionUserId}
    , NOW()
    , NULL
    , NULL
WHERE NOT EXISTS(SELECT * FROM upsert)
